#coding:utf-8"""DialogConsole  .py"""import wxclass ConsoleDialog(wx.Dialog):	def OnClick_send(self, event):		self.Close(wx.ID_OK)			def __init__(self,message_body):		wx.Dialog.__init__(self, None, size=(650, 500))				self.panel = wx.Panel(self,-1,pos=(0,0),size = (650,500))		#self.panel.SetBackgroundColour(wx.SystemSettings.GetColour(wx.SYS_COLOUR_INFOBK))				self.Box = wx.BoxSizer(wx.HORIZONTAL)				self.consoleText = wx.TextCtrl(self.panel,value=message_body,pos =(5,5),size=(600,450),style=wx.TE_MULTILINE)		self.Box.Add(self.consoleText, proportion=0,flag=wx.EXPAND|wx.ALL,border=30)				mail_send_button = wx.Button(self.panel, label="Close",pos = (610,5),size = (35,70))		mail_send_button.Bind(wx.EVT_BUTTON, self.OnClick_send)		self.Box.Add(mail_send_button, proportion=0,flag=wx.EXPAND|wx.LEFT,border=5)				self.panel.SetSizer(self.Box)		self.panel.Refresh()	def Add(self,message):		self.consoleText.AppendText(message+'\n')		# app = wx.App(False)# dialog = ConsoleDialog("hello \n")# result = dialog.ShowModal()# if result == wx.ID_OK:	# # Maybe send here instead of print?	# print "\nDialog closed."